import React, { Component } from 'react';
import Emoji from 'a11y-react-emoji';

class Experience extends Component {
    constructor() {
        super()
        this.state = {
            jobs: [
                {
                    id: 1,
                    name: "bugaloo",
                    editFlag: true,
                },
                {
                    id: 2,
                    name: "electric",
                    editFlag: false,
                },
            ]
        }
    }

    render() {
        return (
            <div>
                <h1>
                    Experience <Emoji symbol="🗺️" label="world-map" />
                </h1>
                {this.state.jobs.map(
                    (job) => {if(job.editFlag === true){
                        return (
                            <li key={job.id}>
                                <input id={job.id} className="edit-task" type="text" defaultValue={job.name}></input>
                            </li>
                        )
                    } else {
                        return (
                            <li key={job.id}>
                                {job.name}
                            </li>
                        )
                    }
                }
                )

                }
            </div>
        )
    }
}

  export default Experience;
