import React, { Component } from 'react';
import Emoji from 'a11y-react-emoji';

class Experience extends Component {
    constructor() {
        super()
        this.state = {
            jobs: [
                {
                    id: 1,
                    employer: "bugaloo",
                    role: "maker",
                    responsibilities: [
                        "make stuff",
                        "make more stuff",
                        "make even more stuff",
                    ],
                    yearStarted: 2014,
                    yearEnded: 2015,
                    editFlag: true,
                },
                {
                    id: 2,
                    employer: "electric",
                    role: "shaker",
                    responsibilities: [
                        "shake stuff up",
                        "shake stuff up more",
                        "shake stuff up even more",
                    ],
                    yearStarted: 2015,
                    yearEnded: 2020,
                    editFlag: false,
                },
            ]
        }

        this.editJob = this.editJob.bind(this);
        this.updateJob = this.updateJob.bind(this);
    }

    editJob(job){
        const jobsCopy = this.state.jobs;
        let selectedJob = jobsCopy.find(blah => blah.id === job.job.id);
        selectedJob.editFlag = !selectedJob.editFlag;
        this.setState({
          jobs: jobsCopy,
        })
    }

    updateJob(job){
        const jobsCopy = this.state.jobs;
        let selectedJob = jobsCopy.find(blah => blah.id === job.job.id);
        let selectedJobNode = document.getElementById(selectedJob.id)

        /////////////////////// next steps are to solve for editing responsibilities list since there are a dynamic # of fields //////////////////

        const employer = selectedJobNode.parentNode.querySelector(".edit-employer").value;
        const role = selectedJobNode.parentNode.querySelector(".edit-role").value;
        // const employer = selectedJobNode.parentNode.querySelector(".edit-job").value;
        const yearStarted = selectedJobNode.parentNode.querySelector(".edit-year-started").value;
        const yearEnded = selectedJobNode.parentNode.querySelector(".edit-year-ended").value;
        // const yearStarted = document.getElementById("input-year-started").value
        // const yearFinished = document.getElementById("input-year-finished").value
        // const degreeType = document.getElementById("input-degree-type").value
        // const areaOfStudy = document.getElementById("input-area-of-study").value

        selectedJob.employer = employer;
        selectedJob.role = role;
        selectedJob.yearStarted = yearStarted;
        selectedJob.yearEnded = yearEnded;
        selectedJob.editFlag = !selectedJob.editFlag;
        this.setState({
          jobs: jobsCopy,
        })

        // this.setState({
        //     school: school,
        //     yearStarted: yearStarted,
        //     yearFinished: yearFinished,
        //     degreeType: degreeType,
        //     areaOfStudy: areaOfStudy,
        //     editFlag: !this.state.editFlag,
        // })
    }

    render() {
        return (
            <div>
                <h1>
                    Experience <Emoji symbol="🗺️" label="world-map" />
                </h1>
                {this.state.jobs.map(
                    (job) => {if(job.editFlag === true){
                        return (
                            <div id={job.id}>
                                <input className="edit-employer" type="text" defaultValue={job.employer} />
                                <input className="edit-role" type="text" defaultValue={job.role} />
                                <input className="edit-year-started float-right" type="number" min="1900" max="2099" step="1" defaultValue={job.yearStarted} />
                                <input className="edit-year-ended float-right" type="number" min="1900" max="2099" step="1" defaultValue={job.yearEnded} />
                                <i onClick={() => this.updateJob({job})} className="far fa-check-square"></i>
                            </div>
                        )
                    } else {
                        return (
                            <div>
                                {job.employer} - {job.role} <i onClick={() => this.editJob({job})} className="far fa-edit"></i>
                                <span className="float-right">
                                    {job.yearStarted} - {job.yearEnded}
                                </span>
                                <ul>
                                    {job.responsibilities.map(
                                        (responsibility) => 
                                        <li>{responsibility}</li>)}
                                </ul>

                            </div>
                        )
                    }
                }
                )

                }
            </div>
        )
    }
}

  export default Experience;
