import React, { Component } from 'react';
import Emoji from 'a11y-react-emoji';

class Experience extends Component {
    constructor() {
        super()
        this.state = {
            jobs: [
                {
                    id: 1,
                    employer: "bugaloo",
                    role: "maker",
                    responsibilities: [
                        "make stuff",
                        "make more stuff",
                        "make even more stuff",
                    ],
                    yearStarted: 2014,
                    yearEnded: 2015,
                    editFlag: true,
                },
                {
                    id: 2,
                    employer: "electric",
                    role: "shaker",
                    responsibilities: [
                        "shake stuff up",
                        "shake stuff up more",
                        "shake stuff up even more",
                    ],
                    yearStarted: 2015,
                    yearEnded: 2020,
                    editFlag: false,
                },
            ]
        }

        this.editJob = this.editJob.bind(this);
        this.updateJob = this.updateJob.bind(this);
    }

    editJob(job){
        const jobsCopy = this.state.jobs;
        let selectedJob = jobsCopy.find(blah => blah.id === job.job.id);
        selectedJob.editFlag = !selectedJob.editFlag;
        this.setState({
          jobs: jobsCopy,
        })
    }

    updateJob(job){
        const jobsCopy = this.state.jobs;
        let selectedJob = jobsCopy.find(blah => blah.id === job.job.id);
        let selectedJobNode = document.getElementById(selectedJob.id)

        const employer = document.querySelector(selectedJob.employer).value;
        // const employer = selectedJobNode.parentNode.querySelector(selectedJob.employer).value;
        // const yearStarted = document.getElementById("input-year-started").value
        // const yearFinished = document.getElementById("input-year-finished").value
        // const degreeType = document.getElementById("input-degree-type").value
        // const areaOfStudy = document.getElementById("input-area-of-study").value

        selectedJob.employer = employer;
        selectedJob.editFlag = !selectedJob.editFlag;
        this.setState({
          jobs: jobsCopy,
        })

        // this.setState({
        //     school: school,
        //     yearStarted: yearStarted,
        //     yearFinished: yearFinished,
        //     degreeType: degreeType,
        //     areaOfStudy: areaOfStudy,
        //     editFlag: !this.state.editFlag,
        // })
    }

    render() {
        return (
            <div>
                <h1>
                    Experience <Emoji symbol="🗺️" label="world-map" />
                </h1>
                {this.state.jobs.map(
                    (job) => {if(job.editFlag === true){
                        return (
                            <div id={job.id}>
                                <input id={job.employer} className="edit-job" type="text" defaultValue={job.employer}></input>
                                {/* <input id="test-test" className="edit-job" type="text" defaultValue={job.employer}></input> */}
                                <i onClick={() => this.updateJob({job})} className="far fa-check-square"></i>
                            </div>
                        )
                    } else {
                        return (
                            <div>
                                {job.employer} - {job.role} <i onClick={() => this.editJob({job})} className="far fa-edit"></i>
                                <span className="float-right">
                                    {job.yearStarted} - {job.yearEnded}
                                </span>
                                <ul>
                                    {job.responsibilities.map(
                                        (responsibility) => 
                                        <li>{responsibility}</li>)}
                                </ul>

                            </div>
                        )
                    }
                }
                )

                }
            </div>
        )
    }
}

  export default Experience;
