import React, { Component } from 'react';
import Emoji from 'a11y-react-emoji';

class Experience extends Component {
    constructor() {
        super()
        this.state = {
            jobs: [
                {
                    id: 1,
                    name: "bugaloo",
                    role: "maker",
                    responsibilities: [
                        "make stuff",
                        "make more stuff",
                        "make even more stuff",
                    ],
                    yearStarted: 2014,
                    yearEnded: 2015,
                    editFlag: true,
                },
                {
                    id: 2,
                    name: "electric",
                    role: "shaker",
                    responsibilities: [
                        "shake stuff up",
                        "shake stuff up more",
                        "shake stuff up even more",
                    ],
                    yearStarted: 2015,
                    yearEnded: 2020,
                    editFlag: false,
                },
            ]
        }
    }

    render() {
        return (
            <div>
                <h1>
                    Experience <Emoji symbol="🗺️" label="world-map" />
                </h1>
                {this.state.jobs.map(
                    (job) => {if(job.editFlag === true){
                        return (
                            <div>
                                <input id={job.id} className="edit-task" type="text" defaultValue={job.name}></input>
                            </div>
                        )
                    } else {
                        return (
                            <div>
                                {job.name} - {job.role}
                                <span className="float-right">
                                    {job.yearStarted} - {job.yearEnded}
                                </span>
                                <ul>
                                    {job.responsibilities.map(
                                        (responsibility) => 
                                        <li>{responsibility}</li>)}
                                </ul>

                            </div>
                        )
                    }
                }
                )

                }
            </div>
        )
    }
}

  export default Experience;
