import React, { Component } from 'react';

class Banner extends Component {
    constructor() {
        super()
        this.state = {
            src: "https://i.pinimg.com/originals/3b/29/ee/3b29ee0897204e6ece68886641afcb63.png",
            editFlag: true,
        }
    }

    render() {
        if(this.state.editFlag === true){
            return (
                <div class="container">
                    <img className="image-banner" src={this.state.src} />
                    <label for="banner-src">image URL: </label>
                    <input name="banner-src" type="text" defaultValue={this.state.src}></input>
                    <button class="btn-green float-right">all done</button>
                </div>
            )
        } else {
            return (
                <div class="container">
                    <img className="image-banner" src={this.state.src} />
                    <div class="overlay">
                        <a href="#" class="icon" title="User Profile">
                        <i class="fa fa-user"></i>
                        </a>
                    </div>
                </div>
            )
        }
    }
}

  export default Banner;
