import React, { Component } from 'react';
import Emoji from 'a11y-react-emoji';

class Education extends Component {
    constructor() {
        super()
        this.state = {
            school: "Greendale Community College",
            yearStarted: 2010,
            yearFinished: 2014,
            degreeType: "Bachelor's",
            areaOfStudy: "Underwater Basket Weaving",
            editFlag: true,
        }

        this.updateEducation = this.updateEducation.bind(this);
    }

    updateEducation(){
        const school = document.getElementById("input-school").value
        const yearStarted = document.getElementById("input-year-started").value
        const yearFinished = document.getElementById("input-year-finished").value
        const degreeType = document.getElementById("input-degree-type").value
        const areaOfStudy = document.getElementById("input-area-of-study").value

        this.setState({
            school: school,
            yearStarted: yearStarted,
            yearFinished: yearFinished,
            degreeType: degreeType,
            areaOfStudy: areaOfStudy,
            editFlag: !this.state.editFlag,
        })
    }

    render() {
        if(this.state.editFlag === true){
        return (
            <div>
                <h1>
                    Education <Emoji symbol="ðŸ“š" label="stack-of-books" />
                </h1>
                    <div>
                        <input id="input-school" type="text" defaultValue={this.state.school}></input>
                        <span className="float-right">
                            <input id="input-year-started" type="number" min="1900" max="2099" step="1" defaultValue={this.state.yearStarted} />
                            -
                            <input id="input-year-finished" type="number" min="1900" max="2099" step="1" defaultValue={this.state.yearFinished} />
                        </span>
                    </div>
                    <div>
                        <select id="input-degree-type" name="degree_type" defaultValue={this.state.school}>
                            <option>Bachelor's</option>
                            <option>Master's</option>
                            <option>Ph.D</option>
                        </select>
                        <input id="input-area-of-study" type="text" defaultValue={this.state.areaOfStudy}></input>
                    </div>
                    {/* <button onClick={this.updateEducation}>Submit</button> */}
                    <i onClick={this.updateEducation} className="far fa-edit"></i>
            </div>
        )
        } else {
        return (
            <div>
                <h1>
                    Education <Emoji symbol="ðŸ“š" label="stack-of-books" />
                </h1>
                <div>
                    {this.state.school}
                    <span className="float-right">
                        {this.state.yearStarted} - {this.state.yearFinished}
                    </span>
                </div>


                <div>{this.state.degreeType} - {this.state.areaOfStudy}</div>
            </div>
        )}
    }
}

  export default Education;
