import React, { Component } from 'react';
import Emoji from 'a11y-react-emoji';

class Education extends Component {
    constructor() {
        super()
        this.state = {
            school: "Greendale Community College",
            yearStarted: 2010,
            yearFinished: 2014,
            degreeType: "Bachelor's",
            areaOfStudy: "Underwater Basket Weaving",
            editFlag: true,
        }
    }

    updateEducation(){
        this.setState({
            school: document.getElementById("input-school"),
            yearStarted: 2010,
            yearFinished: 2014,
            degreeType: "Bachelor's",
            areaOfStudy: "Underwater Basket Weaving",
            editFlag: !this.state.editFlag,
        })
    }

    render() {
        if(this.state.editFlag === true){
        return (
            <div>
                <h1>
                    Education <Emoji symbol="📚" label="stack-of-books" />
                </h1>
                <form>
                    <div>
                        <input id="input-school" type="text" defaultValue={this.state.school}></input>
                        <span class="float-right">
                            <input id="input-year-started" type="number" min="1900" max="2099" step="1" defaultValue={this.state.yearStarted} />
                            -
                            <input type="number" min="1900" max="2099" step="1" defaultValue={this.state.yearFinished} />
                        </span>
                    </div>
                    <div>
                        <select name="degree_type" defaultValue={this.state.school}>
                            <option>Bachelor's</option>
                            <option>Master's</option>
                            <option>Ph.D</option>
                        </select>
                        <input type="text" defaultValue={this.state.areaOfStudy}></input>
                    </div>
                </form>
            </div>
        )
        } else {
        return (
            <div>
                <h1>
                    Education poo poo magoo <Emoji symbol="📚" label="stack-of-books" />
                </h1>
                <div>
                    {this.state.school}
                    <span class="float-right">
                        {this.state.yearStarted} - {this.state.yearFinished}
                    </span>
                </div>


                <div>{this.state.degreeType} - {this.state.areaOfStudy}</div>
            </div>
        )}
    }
}

  export default Education;
