import React, { Component } from 'react';
import Emoji from 'a11y-react-emoji';

class General extends Component {
    constructor() {
        super()
        this.state = {
            userName: "Bob",
            editFlag: false,
        }

        this.editUserName = this.editUserName.bind(this);
        this.updateUserName = this.updateUserName.bind(this);
    }

    editUserName(){
        this.setState({
            editFlag: !this.state.editFlag,
        })
    }

    updateUserName(){
        const userName = document.getElementById("input-user-name").value

        this.setState({
            userName: userName,
            editFlag: !this.state.editFlag,
        })
    }

    render() {
        if(this.state.editFlag === true){
        return (
            <div>
                <h1>
                    Hi, my name is <input id="input-user-name" type="text" defaultValue={this.state.userName}></input> <Emoji symbol="ðŸ¤™" label="shaka-hand" />
                    <button onClick={this.updateUserName} className="float-right"><i class="fas fa-cog"></i></button>
                </h1>
            </div>
        )
        } else {
        return (
            <div>
                <h1>
                    Hi, my name is <span id="user-name">{this.state.userName}</span> <Emoji symbol="ðŸ¤™" label="shaka-hand" />
                    <button onClick={this.editUserName} className="float-right"><i class="fas fa-cog"></i></button>
                </h1>
            </div>
        )
    }
    }
}

  export default General;
