import React, { Component } from 'react';
import Emoji from 'a11y-react-emoji';
import uniqid from "uniqid";

class Experience extends Component {
    constructor() {
        super()
        this.state = {
            jobs: [
                {
                    id: uniqid(),
                    employer: "bugaloo",
                    role: "maker",
                    responsibilities: [
                        "make stuff",
                        "make more stuff",
                        "make even more stuff",
                    ],
                    yearStarted: 2014,
                    yearEnded: 2015,
                    editFlag: false,
                },
                {
                    id: uniqid(),
                    employer: "electric",
                    role: "shaker",
                    responsibilities: [
                        "shake stuff up",
                        "shake stuff up more",
                        "shake stuff up even more",
                    ],
                    yearStarted: 2015,
                    yearEnded: 2020,
                    editFlag: false,
                },
            ],
            placeholderJob: {
                responsibilities: [
                    "describe responsibility here",
                    "describe responsibility here",
                    "describe responsibility here",
                ],
            },
            activeEditFlag: false,
            addJobFlag: false,
        }

        this.editJob = this.editJob.bind(this);
        this.updateJob = this.updateJob.bind(this);
        this.addJob = this.addJob.bind(this);
        this.addResponsibility = this.addResponsibility.bind(this);
    }

    addTask(){
        const newJob = {
          id: uniqid(),
          task: document.getElementById("input").value
        }

        this.setState({
            tasks: [...this.state.tasks, newJob]
        })
        console.log(this.state.tasks)
    }

    editJob(job){
        const jobsCopy = this.state.jobs;
        let selectedJob = jobsCopy.find(blah => blah.id === job.job.id);
        selectedJob.editFlag = !selectedJob.editFlag;
        this.setState({
          jobs: jobsCopy,
        })
    }

    updateJob(job){
        const jobsCopy = this.state.jobs;
        let selectedJob = jobsCopy.find(blah => blah.id === job.job.id);
        let selectedJobNode = document.getElementById(selectedJob.id)

        const employer = selectedJobNode.parentNode.querySelector(".edit-employer").value;
        const role = selectedJobNode.parentNode.querySelector(".edit-role").value;
        const yearStarted = selectedJobNode.parentNode.querySelector(".edit-year-started").value;
        const yearEnded = selectedJobNode.parentNode.querySelector(".edit-year-ended").value;

        for(let i =0; i < selectedJob.responsibilities.length; i++){
            const children = selectedJobNode.parentNode.querySelector(".edit-responsibilities");
            let selectedResponsibility = children.childNodes[i].firstChild.value;
            selectedJob.responsibilities[i] = selectedResponsibility;
        }

        selectedJob.employer = employer;
        selectedJob.role = role;
        selectedJob.yearStarted = yearStarted;
        selectedJob.yearEnded = yearEnded;
        selectedJob.editFlag = !selectedJob.editFlag;

        this.setState({
          jobs: jobsCopy,
        })
    }

    addJob(){
        console.log("sdfasd")
        this.setState({
            addJobFlag: !this.state.addJobFlag,
        })
    }

    addResponsibility(e,job){
        const checkIfNewOrExistingJob = e.target.closest(".job-block").classList.contains("new-job");

        if(checkIfNewOrExistingJob){
            const placeholderJobCopy = this.state.placeholderJob;
            const newResp = "describe responsibility here";
            placeholderJobCopy.responsibilities = [...this.state.placeholderJob.responsibilities, newResp]

            this.setState({
                placeholderJob: placeholderJobCopy
            })
        } else {
            const jobsCopy = this.state.jobs;
            const newResp = "describe responsibility here";
            let selectedJob = jobsCopy.find(blah => blah.id === job.id);
            selectedJob.responsibilities.push(newResp);

            this.setState({
                jobs: jobsCopy,
            })
        }


    }

    render() {
        return (
            <div>
                <h1>
                    Experience <Emoji symbol="🗺️" label="world-map" />
                    <button onClick={this.addJob} className="float-right">add job</button>
                </h1>

                {
                    this.state.addJobFlag &&

                    // <div id="new-job" className="job-block new-job">
                    <div className="job-block new-job">
                        <input className="edit-employer" type="text" defaultValue="employer" />
                        <input className="edit-role" type="text" defaultValue="role" />
                        <span className="float-right">
                            <input className="edit-year-started" type="number" min="1900" max="2099" step="1" defaultValue="2020" />
                            <input className="edit-year-ended" type="number" min="1900" max="2099" step="1" defaultValue="2020" />
                        </span>
                        <div>
                            <ul className="edit-responsibilities">
                                {
                                    this.state.placeholderJob.responsibilities.map(
                                    (responsibility) => 
                                    <li key={uniqid()}>
                                        <input id={uniqid()} className="edit-responsibility" type="text" defaultValue={responsibility} />
                                    </li>
                                    )
                                }
                            </ul>
                            <button onClick={(e) => this.addResponsibility(e)}>add responsibility</button>
                        </div>

                        <i onClick={() => this.addJob} className="far fa-check-square"></i>
                    </div>
                }

                {this.state.jobs.map(
                    (job) => {if(job.editFlag === true){
                        return (
                            <div id={job.id} className="job-block">
                                <input className="edit-employer" type="text" defaultValue={job.employer} />
                                <input className="edit-role" type="text" defaultValue={job.role} />
                                <span className="float-right">
                                    <input className="edit-year-started" type="number" min="1900" max="2099" step="1" defaultValue={job.yearStarted} />
                                    <input className="edit-year-ended" type="number" min="1900" max="2099" step="1" defaultValue={job.yearEnded} />
                                </span>
                                <div>
                                    <ul className="edit-responsibilities">
                                        {
                                            job.responsibilities.map(
                                            (responsibility) => 
                                            <li key={uniqid()}>
                                                <input id={uniqid()} className="edit-responsibility" type="text" defaultValue={responsibility} />
                                            </li>
                                            )
                                        }
                                    </ul>
                                    <button onClick={(e) => this.addResponsibility(e,job)}>add responsibility</button>
                                </div>
                                <i onClick={() => this.updateJob({job})} className="far fa-check-square"></i>
                            </div>
                        )
                    } else {
                        return (
                            <div>
                                {job.employer} - {job.role} <i onClick={() => this.editJob({job})} className="far fa-edit"></i>
                                <span className="float-right">
                                    {job.yearStarted} - {job.yearEnded}
                                </span>
                                <ul>
                                    {job.responsibilities.map(
                                        (responsibility) => 
                                        <li key={uniqid()}>{responsibility}</li>)}
                                </ul>

                            </div>
                        )
                    }
                }
                )

                }
            </div>
        )
    }
}

  export default Experience;
